import React, { useEffect } from "react";
import CivCard from "./CivCard.js";
import { connect } from "react-redux";
import { getCiv, nextCiv, prevCiv } from "../actions";

const Platter = () => {
  useEffect(() => getCiv);

  return (
    <div>
      <h1>Civilization Navigation</h1>
      <button>Prev</button>
      <CivCard />
      <button>Next</button>
    </div>
  );
};

const mapStateToProps = state => {
  return {
    civData: {
      name: state.name,
      army_type: state.army_type,
      team_bonus: state.team_bonus,
      civilization_bonus: state.civilization_bonus
    }
  };
};

export default connect(mapStateToProps, {
  getCiv,
  nextCiv,
  prevCiv
})(Platter);
